const Cards = (props) => (
    <>
        <p>{props.fName}</p>
        <p>{props.lName}</p>
        <p>{props.phone}</p>
        <img src={props.image} />
    </>
)



Cards.getInitialProps = async function () {
    const response = await fetch('https://api.randomuser.me/')
    const data = await response.json()

    return {
        fName: data.result[0].name.first,
        lName: data.result[0].name.last,
        phone: data.result[0].phone,
        image: data.result[0].picture.medium,

    }
}
