import fetch from 'isomorphic-unfetch'

const Cards = (props) => (
    <>
        <p>hello card</p>
        <p>{props.fName}</p>
        <p>{props.lName}</p>
        <p>{props.phone}</p>
        <img src={props.image} />
    </>
)



export async function getStaticProps() {
    const response = await fetch('https://api.randomuser.me/')
    const data = await response.json()

    return {
        props: {

            fName: data.result[0].name.first,
            lName: data.result[0].name.last,
            phone: data.result[0].phone,
            image: data.result[0].picture.medium,
        }

    }
}


export default Cards
